INSERT INTO public.dicas (titulo, descricao, conteudo, categoria) VALUES
('Como Fazer um Currículo Profissional', 'Aprenda a criar um currículo que chama atenção dos recrutadores.', 'Um bom currículo deve ser claro, objetivo e destacar suas principais qualificações. Inclua: dados pessoais, objetivo profissional, formação acadêmica, experiências (mesmo que voluntárias), cursos e habilidades. Mantenha o formato limpo e use no máximo 2 páginas. Revise sempre para evitar erros de português.', 'curriculo'),
('Dicas para Entrevista de Emprego', 'Prepare-se para sua primeira entrevista com estas dicas essenciais.', 'Antes da entrevista: pesquise sobre a empresa, prepare respostas para perguntas comuns, escolha uma roupa adequada. Durante: seja pontual, cumprimente com firmeza, mantenha contato visual, seja honesto e mostre interesse. Depois: agradeça a oportunidade e aguarde o retorno no prazo informado.', 'entrevista'),
('Como Usar o LinkedIn para Conseguir Emprego', 'O LinkedIn é uma ferramenta poderosa para sua carreira profissional.', 'Crie um perfil completo com foto profissional, título atraente e resumo que destaque suas habilidades. Conecte-se com profissionais da sua área, participe de grupos, compartilhe conteúdo relevante e interaja com publicações. Use a busca de vagas e ative o modo "Disponível para oportunidades".', 'linkedin'),
('Canva: Ferramenta Gratuita para Criar Currículos', 'Use o Canva para criar currículos visuais e profissionais gratuitamente.', 'O Canva oferece templates gratuitos de currículos profissionais. Acesse canva.com, crie uma conta gratuita, busque por "currículo" e escolha um modelo. Personalize com suas informações, cores e fontes. Baixe em PDF e use em suas candidaturas. É fácil, rápido e o resultado é profissional.', 'canva');
